import React, { Component } from 'react';
import axios from 'axios';

export default class CreateDonation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      contactNo: "",
      address: "",
      cardType: "",
      expDate: "",
      cvCode: "",
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;

    this.setState({
      [name]: value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { name, contactNo, address, cardType, expDate, cvCode } = this.state;

    const data = {
      name: name,
      contactNo: contactNo,
      address: address,
      cardType: cardType,
      expDate: expDate,
      cvCode: cvCode,
    };

    console.log(data);

    axios.post("http://localhost:8000/donation/save", data)
      .then((res) => {
        alert("Added Successfully");
        if (res.data.success) {
          this.setState({
            name: "",
            contactNo: "",
            address: "",
            cardType: "",
            expDate: "",
            cvCode: "",
          });
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  render() {
    return (
      <div className="col-md-6 mt-4 mx-auto">
        <h1 className="h3 mb-3 font-weight-normal">Add New Donation</h1>
        <form className="needs-validation" noValidate>
          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ marginBottom: '5px' }}>Name</label>
            <input
              type="text"
              className="form-control"
              name="name"
              required
              placeholder="Enter name"
              value={this.state.name}
              onChange={this.handleInputChange}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ marginBottom: '5px' }}>Contact No</label>
            <input
              type="text"
              className="form-control"
              name="contactNo"
              required
              placeholder="Enter Contact No"
              value={this.state.contactNo}
              onChange={this.handleInputChange}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ marginBottom: '5px' }}>Address</label>
            <input
              type="text"
              className="form-control"
              name="address"
              required
              placeholder="Enter Address"
              value={this.state.address}
              onChange={this.handleInputChange}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ marginBottom: '5px' }}>Card Type</label>
            <input
              type="text"
              className="form-control"
              name="cardType"
              required
              placeholder="Enter Card Type"
              value={this.state.cardType}
              onChange={this.handleInputChange}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ marginBottom: '5px' }}>Exp Date</label>
            <input
              type="text"
              className="form-control"
              name="expDate"
              required
              placeholder="Enter Exp Date"
              value={this.state.expDate}
              onChange={this.handleInputChange}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ marginBottom: '5px' }}>Cv Code</label>
            <input
              type="text"
              className="form-control"
              name="cvCode"
              required
              placeholder="Enter Cv Code"
              value={this.state.cvCode}
              onChange={this.handleInputChange}
            />
          </div>

          <button
            className="btn btn-success"
            type="submit"
            style={{ marginTop: '15px' }}
            onClick={this.onSubmit}
          >
            <i className="far fa-check-square"></i>&nbsp; Save
          </button>
        </form>
        &nbsp;
      </div>
    );
  }
}
